html {
  scroll-behavior: smooth;
}

body {
  font-family: "Montserrat", sans-serif;
  line-height: 1.5;
  color: $dark-color;
  background-color: rgba($primary-color-variant, 0.1);
  user-select: none;
}

.container {
  width: $container-lg;
  margin: 0 auto;
}

img {
  display: block;
  width: 100%;
}

h1,
h2,
h3,
h4,
h5 {
  line-height: 1.2;
}

section {
  margin-top: 6rem;

  & > h2 {
    text-align: center;
  }

  & > p {
    text-align: center;
    width: 42%;
    margin: 0.6rem auto 2.5rem;
  }
}

.btn {
  @include button();
}

.btn-primary {
  @include button($white-color, $primary-color);
}

/*****************************MEDIA QUERIES (Medium Devices)*********************/
@media screen and (max-width: 1024px) {
  .container {
    width: $container-md;
  }

  section {
    & > p {
      width: 55%;
    }
  }
}

/*****************************MEDIA QUERIES (Small Devices)*********************/
@media screen and (max-width: 600px) {
  section {
    & > p {
      width: $container-md;
    }
  }
}

// ==================================Top most services styles=================
#services {
  .container {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 2rem;

    article {
      @include gradientBackground;
      padding: 1.5rem;
      border-radius: 1rem;
      display: flex;
      gap: 1.5rem;
      height: fit-content;
      transition: all 400ms ease;

      i {
        font-size: 1.7rem;
      }

      div {
        h4 {
          margin-bottom: 1rem;
        }
      }
    }
  }
}

/*****************************MEDIA QUERIES (Medium Devices)*********************/
@media screen and (max-width: 1024px) {
  #services {
    .container {
      gap: 1rem;
      grid-template-columns: 1fr 1fr;

      article {
        padding: 1.2rem;
        gap: 0.8rem;
      }
    }
  }
}

/*****************************MEDIA QUERIES (small Devices)*********************/
@media screen and (max-width: 600px) {
  #services {
    .container {
      grid-template-columns: 1fr;

      article {
        h4 {
          margin-bottom: 0.8rem;
        }
      }
    }
  }
}

// ==================================End of Top most services styles========

// ==================================Our top specialists styles=================
#specialists {
  .container {
    width: 68%;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 2rem;

    article {
      background-color: $white-color;
      border-radius: 1rem;
      box-shadow: 0 1.5rem 1.5rem rgba($black-color, 0.1);
      position: relative;
      transition: all 700ms ease;

      &:hover {
        box-shadow: none;

        .specialist__image::before {
          left: 0;
          border-radius: 1rem;
        }

        .specialist__socials,
        .specialist__whatsapp {
          opacity: 1;
          visibility: visible;
        }
      }

      .specialist__image {
        height: 10rem;
        position: relative;
        border-radius: 1rem;
        overflow: hidden;

        &::before {
          content: "";
          display: block;
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: -100%;
          background-image: linear-gradient(
            to bottom,
            rgba($primary-color-variant, 0.8),
            rgba($white-color, 0.8)
          );
          border-radius: 50%;
          transition: all 1s ease;
        }
      }

      .specialist__details {
        margin: 1.5rem 0;
        text-align: center;
      }

      .specialist__socials {
        position: absolute;
        top: 0.4rem;
        right: 1.2rem;
        display: flex;
        flex-direction: column;
        gap: 1rem;
        opacity: 0;
        visibility: hidden;
        transition: all 1s 400ms ease;

        a {
          background: $primary-color-variant;
          color: $white-color;
          padding: 0.4rem;
          display: flex;
          font-size: 1.1rem;
          border-radius: 50%;
          transition: all 400ms ease;

          &:hover {
            background: $white-color;
            color: $primary-color-variant;
          }
        }
      }

      .specialist__whatsapp {
        background: $success-color;
        color: $white-color;
        position: absolute;
        left: 50%;
        transform: translate(-50%);
        bottom: 30%;
        padding: 0.5rem;
        border-radius: 50%;
        display: flex;
        font-size: 1.3rem;
        opacity: 0;
        visibility: hidden;
        transition: all 1s 400ms ease;
      }
    }
  }
}

/*****************************MEDIA QUERIES (Medium Devices)*********************/
@media screen and (max-width: 1024px) {
  #specialists {
    .container {
      grid-template-columns: 1fr 1fr;
    }
  }
}

/*****************************MEDIA QUERIES (Small Devices)*********************/
@media screen and (max-width: 600px) {
  #specialists {
    .container {
      grid-template-columns: 1fr;
    }
  }
}
// =======================End of Our top specialists styles=================

// ==================================Testimonial Styles====================
.swiper {
  width: $container-lg;

  .swiper-slide {
    // brings swiper pagination down
    margin-bottom: 2.5rem;
  }

  .container,
  .swiper-wrapper {
    article {
      @include gradientBackground;
      padding: 1.5rem 2rem;
      border-radius: 1rem;
      cursor: default;
      font-size: 0.9rem;

      p {
        margin-bottom: 1.5rem;
      }

      .patient {
        display: flex;
        align-items: center;
        gap: 1rem;

        .avatar {
          width: 2.5rem;
          aspect-ratio: 1/1;
          border-radius: 50%;
          overflow: hidden;
        }
      }
    }
  }

  .swiper-pagination-bullet {
    width: 20px;
    height: 20px;
    text-align: center;
    line-height: 20px;
    font-size: 12px;
    color: #000;
    opacity: 1;
    background: rgba(0, 0, 0, 0.2);
  }

  .swiper-pagination-bullet-active {
    color: #fff;
    background: #007aff;
  }
}

/*****************************MEDIA QUERIES (Small Devices)*********************/
@media screen and (max-width: 600px) {
  .swiper {
    .container,
    .swiper-wrapper {
      .article {
        padding: 1.5rem;
      }
    }
  }
}
// ==================================End of Testimonial Styles====================



/* =============================== FAQS =========================================== */
.faqs {
  @include gradientBackground;
  box-shadow: inset 0 0 5rem rgba(0, 0, 0, 0.8);
  padding: 2rem;

  h2 {
    margin-bottom: 20px;
    color: white;
  }

  .faqs__container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;

    .faq {
      padding: 2rem;
      display: flex;
      align-items: center;
      gap: 1.4rem;
      height: fit-content;
      background-color: $primary-color-variant;
      cursor: pointer;

      p {
        margin-top: 0.8rem;
        display: none;
      }

      &.open p {
        display: block;
      }
    }

    h4 {
      font-size: 1rem;
      line-height: 2.2;
    }

    .faq__icon {
      align-self: flex-start;
      font-size: 1.2rem;
    }
  }
}

/*****************************MEDIA QUERIES (Medium Devices)*********************/
@media screen and (max-width: 1024px) {
  .faqs {
    .faqs__container {
      grid-template-columns: 1fr;
    }

    .faq {
      padding: 1.5rem;
    }
  }
}

