header {
  @include gradientBackground;
  height: 100vh;
  display: grid;
  place-items: center;
  overflow: hidden;
  clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);

  .container {
    margin-top: 3rem;
    display: grid;
    grid-template-columns: 46% 46%;
    align-items: center;
    gap: 8%;
    justify-content: space-between;

    .header__content {
      h1 {
        font-size: 3.5rem;
        line-height: 1.1;
        letter-spacing: -3px;
        font-weight: 300;
      }

      p {
        margin: 1rem 0 2rem;
        color: $light-color;
        font-size: 1.1rem;
      }
    }
  }
}

/*****************************MEDIA QUERIES (Small Devices)*********************/
@media screen and (max-width: 600px) {
  header {
    height: auto;
    padding: 3rem 1rem;
    clip-path: none;

    .container {
      grid-template-columns: 1fr;
      text-align: center;

      .header__content {
        h1 {
          font-size: 2rem;
        }
      }
    }
  }
}

/*****************************MEDIA QUERIES (Medium Devices)*********************/
@media screen and (min-width: 601px) and (max-width: 1024px) {
  header {
    height: 60vh;

    .container {
      gap: 1rem;

      .header__content {
        h1 {
          font-size: 2.2rem;
        }
      }
    }
  }
}

